package org.ethereum.casper.core;

import javafx.util.Pair;
import org.apache.commons.lang3.ArrayUtils;
import org.ethereum.casper.config.CasperProperties;
import org.ethereum.config.SystemProperties;
import org.ethereum.core.Block;
import org.ethereum.core.CallTransaction;
import org.ethereum.core.Repository;
import org.ethereum.core.Transaction;
import org.ethereum.crypto.ECKey;
import org.ethereum.facade.Ethereum;
import org.ethereum.util.ByteUtil;
import org.ethereum.util.FastByteComparisons;
import org.ethereum.vm.program.ProgramResult;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.spongycastle.util.encoders.Hex;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Component;

import java.math.BigInteger;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

import static org.ethereum.casper.config.net.CasperTestNetConfig.BASE_INTEREST_FACTOR;
import static org.ethereum.casper.config.net.CasperTestNetConfig.BASE_PENALTY_FACTOR;
import static org.ethereum.casper.config.net.CasperTestNetConfig.MIN_DEPOSIT_ETH;
import static org.ethereum.casper.config.net.CasperTestNetConfig.NULL_SIGN_SENDER;
import static org.ethereum.casper.config.net.CasperTestNetConfig.WITHDRAWAL_DELAY;
import static org.ethereum.crypto.HashUtil.sha3;

@Component
public class CasperFacade {

    private static final Logger logger = LoggerFactory.getLogger("casper");

    private static final byte[] CASPER_VOTE_DATA_HEAD = Hex.decode("e9dc0614");
    private static final byte[] CASPER_EPOCH_DATA_HEAD = Hex.decode("5dcffc17");

    private CasperProperties systemProperties;

    private Ethereum ethereum;

    private CallTransaction.Contract casper = null;

    private String contractAddress;  // FIXME: why should we have casper addresses in two places?. It's already in SystemProperties

    private List<Transaction> initTxs;

    public CallTransaction.Contract getContract() {
        init();
        return casper;
    }

    private void init() {
        if (casper != null) {
            return;
        }

        Pair<byte[], List<Transaction>> res = makeInitTxes();
        byte[] casperAddress = res.getKey();
        this.initTxs = res.getValue();
        systemProperties.setCasperAddress(casperAddress);
        this.contractAddress = Hex.toHexString(casperAddress);
        logger.info("Casper contract address set to [0x{}]", contractAddress);
        String casperAbi = systemProperties.getCasperAbi();
        this.casper = new CallTransaction.Contract(casperAbi);
    }

    public Object[] constCall(String func, Object... funcArgs) {
        init();
        ProgramResult r = ethereum.callConstantFunction(contractAddress,
                casper.getByName(func), funcArgs);
        return casper.getByName(func).decodeResult(r.getHReturn());
    }


    public Object[] constCall(Block block, String func, Object... funcArgs) {
        init();
        Transaction tx = CallTransaction.createCallTransaction(0, 0, 100000000000000L,
                contractAddress, 0, casper.getByName(func), funcArgs);
        ProgramResult r = ethereum.callConstantFunction(block, contractAddress,
                casper.getByName(func), funcArgs);
        return casper.getByName(func).decodeResult(r.getHReturn());
    }

    /**
     * All service txs shouldn't use gas
     * Currently it's votes and epoch change
     */
    public boolean isServiceTx(Transaction transaction) {
        return isServiceTx(transaction, getAddress());
    }

    public boolean isVote(Transaction transaction) {
        return isVote(transaction, getAddress());
    }

    public static boolean isServiceTx(Transaction transaction, byte[] casperAddress) {
        return isVote(transaction, casperAddress) ||
                isEpochSwitch(transaction, casperAddress);
    }

    public static boolean isVote(Transaction transaction, byte[] casperAddress) {
        if (!Arrays.equals(transaction.getSender(), Transaction.NULL_SENDER))
            return false;
        if (casperAddress == null)
            return false;
        if (!Arrays.equals(transaction.getReceiveAddress(), casperAddress))
            return false;

        return FastByteComparisons.compareTo(transaction.getData(), 0, CASPER_VOTE_DATA_HEAD.length,
                CASPER_VOTE_DATA_HEAD, 0, CASPER_VOTE_DATA_HEAD.length) == 0;
    }

    private static boolean isEpochSwitch(Transaction transaction, byte[] casperAddress) {
        if (!Arrays.equals(transaction.getSender(), NULL_SIGN_SENDER.getAddress()))
            return false;
        if (casperAddress == null)
            return false;
        if (!Arrays.equals(transaction.getReceiveAddress(), casperAddress))
            return false;

        return FastByteComparisons.compareTo(transaction.getData(), 0, CASPER_EPOCH_DATA_HEAD.length,
                CASPER_EPOCH_DATA_HEAD, 0, CASPER_EPOCH_DATA_HEAD.length) == 0;
    }

    public byte[] getAddress() {
        init();
        return systemProperties.getCasperAddress();
    }

    public List<Transaction> getInitTxs() {
        init();
        return initTxs;
    }

    public void setEthereum(Ethereum ethereum) {
        this.ethereum = ethereum;
    }

    @Autowired
    public void setSystemProperties(SystemProperties systemProperties) {
        this.systemProperties = (CasperProperties) systemProperties;
    }

    /**
     * @return key: Casper contract address
     *       value: Casper state initialization transactions
     */
    private Pair<byte[], List<Transaction>> makeInitTxes() {
        final byte[] casperAddress = new byte[20];

        // All contracts except Casper itself
        final String VIPER_RLP_DECODER_TX = "";
        final String SIG_HASHER_TX = "";
        final String PURITY_CHECKER_TX = "";

        List<String> txStrs = new ArrayList<>();
        txStrs.add(VIPER_RLP_DECODER_TX);
        txStrs.add(SIG_HASHER_TX);
        txStrs.add(PURITY_CHECKER_TX);

        Repository track = (Repository) ethereum.getSnapshotTo(ethereum.getBlockchain().getBlockByNumber(0).getStateRoot());
        BigInteger nonce = track.getNonce(NULL_SIGN_SENDER.getAddress());
        List<Transaction> txs = new ArrayList<>();
        final long gasPriceFund = 25_000_000_000L;
        for (int i = 0; i < txStrs.size(); ++i) {
            Transaction deployTx = new Transaction(ByteUtil.hexStringToBytes(txStrs.get(i)));
            BigInteger value = BigInteger.ZERO;
            value = value.add(ByteUtil.bytesToBigInteger(deployTx.getValue()));
            value = value.add(
                    ByteUtil.bytesToBigInteger(deployTx.getGasPrice())
                            .multiply(ByteUtil.bytesToBigInteger(deployTx.getGasLimit()))
            );
            Transaction fundTx = new Transaction(
                    ByteUtil.bigIntegerToBytes(nonce),
                    ByteUtil.longToBytesNoLeadZeroes(gasPriceFund),
                    ByteUtil.longToBytesNoLeadZeroes(90_000),
                    deployTx.getSender(),
                    ByteUtil.bigIntegerToBytes(value),
                    new byte[0],
                    null
            );
            fundTx.sign(NULL_SIGN_SENDER);
            txs.add(fundTx);
            txs.add(deployTx);
            nonce = nonce.add(BigInteger.ONE);
        }

        // 0 - fund, 1 - rlp, 2 - fund, 3 - sig hasher, 4 - fund, 5 - purity checker
        byte[] sigHasherContract = txs.get(3).getContractAddress();
        byte[] purityCheckerContract = txs.get(5).getContractAddress();

        // Casper!
        try {
            // Sources:
            // https://github.com/ethereum/casper/blob/9106ad647857e6a545f55d7f6193bdc03bb9f5cd/casper/contracts/simple_casper.v.py
            String casperBinStr = systemProperties.getCasperBin();
            byte[] casperBin = ByteUtil.hexStringToBytes(casperBinStr);

            CallTransaction.Contract contract = new CallTransaction.Contract(systemProperties.getCasperAbi());

            byte[] casperInit = contract.getConstructor().encodeArguments(
                    systemProperties.getCasperEpochLength(),  // Epoch length
                    WITHDRAWAL_DELAY, // Withdrawal delay
                    ECKey.fromPrivate(sha3("0".getBytes())).getAddress(),  // Owner
                    sigHasherContract,  // Signature hasher contract
                    purityCheckerContract,  // Purity checker contract
                    BASE_INTEREST_FACTOR,  // Base interest factor
                    BASE_PENALTY_FACTOR,  // Base penalty factor
                    BigInteger.valueOf(MIN_DEPOSIT_ETH).multiply(BigInteger.TEN.pow(18)) // Minimum validator deposit in wei
            );

            Transaction tx = new Transaction(
                    ByteUtil.bigIntegerToBytes(nonce),
                    ByteUtil.longToBytesNoLeadZeroes(gasPriceFund),
                    ByteUtil.longToBytesNoLeadZeroes(5_000_000),
                    new byte[0],
                    ByteUtil.longToBytesNoLeadZeroes(0),
                    ArrayUtils.addAll(casperBin, casperInit),  // Merge contract and constructor args
                    null);
            tx.sign(NULL_SIGN_SENDER);

            // set casperAddress
            System.arraycopy(tx.getContractAddress(), 0, casperAddress, 0, 20);
            txs.add(tx);
        } catch (Exception ex) {
            throw new RuntimeException("Failed to generate Casper init transactions", ex);
        }

        return new Pair<>(casperAddress, txs);
    }
}
